mapper.delete:
		DELETE FROM STM_SYS_USER_DOMAIN_ROLE_REL WHERE  1=1





     AND USER_ID = #{userId}





     AND	ROLE_ID = #{roleId}





     AND DOMAIN_ID = #{domainId}

mapper.select:
		SELECT ID,
		NAME,
		DESCRIPTION,
		CREATOR_ID AS creatorId,
		CREATED_TIME AS createdTime,
		STATUS
		FROM STM_SYS_DOMAIN

















         AND NAME = #{condition.name}





         AND DESCRIPTION = #{condition.description}





         AND CREATOR_ID = #{condition.creatorId}





         AND STATUS = #{condition.status}

		select relation.USER_ID "userId",relation.ROLE_ID "roleId",relation.DOMAIN_ID "domainId" from STM_SYS_USER_DOMAIN_ROLE_REL relation
		where 1=1 





    AND  USER_ID = #{userId}





     AND 	ROLE_ID = #{roleId}





     AND DOMAIN_ID = #{domainId}
