mapper.select:
SELECT OCUSER.ID AS id, OCUSER.NAME AS name, OCUSER.SEX AS sex, OCUSER.USER_TYPE AS userType, OCUSER.MOBILE AS mobile, OCUSER.EMAIL AS email, OCUSER.ACCOUNT AS account, OCUSER.CREATOR_ID AS creatorId, OCUSER.CREATED_TIME AS createdTime, OCUSER.STATUS AS status FROM STM_SYS_USER OCUSER
JOIN (SELECT DISTINCT USER_ID FROM STM_SYS_USER_DOMAIN_ROLE_REL WHERE DOMAIN_ID in
#{item}
) REL ON OCUSER.ID=REL.USER_ID
AND ( OCUSER.NAME like CONCAT('%',#{condition.keyword},'%') OR OCUSER.ACCOUNT like CONCAT('%',#{condition.keyword},'%') OR OCUSER.MOBILE like CONCAT('%',#{condition.keyword},'%') OR OCUSER.EMAIL like CONCAT('%',#{condition.keyword},'%') )
ORDER BY OCUSER.CREATED_TIME DESC, OCUSER.ID ASC

SELECT OCUSER.ID AS id, OCUSER.NAME AS name, OCUSER.SEX AS sex, OCUSER.USER_TYPE AS userType, OCUSER.MOBILE AS mobile, OCUSER.EMAIL AS email, OCUSER.ACCOUNT AS account, OCUSER.CREATOR_ID AS creatorId, OCUSER.CREATED_TIME AS createdTime, OCUSER.STATUS AS status FROM STM_SYS_USER OCUSER
JOIN (SELECT DISTINCT USER_ID FROM STM_SYS_USER_DOMAIN_ROLE_REL WHERE DOMAIN_ID in
#{item}
) REL ON OCUSER.ID=REL.USER_ID
AND ( OCUSER.NAME like '%'||#{condition.keyword}||'%' OR OCUSER.ACCOUNT like '%'||#{condition.keyword}||'%' OR OCUSER.MOBILE like '%'||#{condition.keyword}||'%' OR OCUSER.EMAIL like '%'||#{condition.keyword}||'%' )
AND OCUSER.USER_TYPE <= #{condition.userType}
ORDER BY OCUSER.CREATED_TIME DESC, OCUSER.ID ASC

